        /// <summary>
        /// 获取每一个字段值
        /// </summary>
        /// <param name="dt"></param>
        /// <returns></returns>
        public static string GetJson(DataTable dt)
        {
            StringBuilder sb = new StringBuilder();
            sb.Append("[");
            if (dt != null && dt.Rows.Count > 0)
            {
                foreach (DataRow dr in dt.Rows)
                {
                    sb.Append("{");
                    foreach (DataColumn dc in dr.Table.Columns)
                    {
                        sb.Append("\"");
                        sb.Append(dc.ColumnName);
                        sb.Append("\":\"");
                        if (dr[dc] != null && dr[dc] != DBNull.Value && dr[dc].ToString() != "")
                        {
                            sb.Append(dr[dc]);
                        }
                        else
                        {
                            sb.Append("&nbsp;");
                        }
                        sb.Append("\",");
                    }
                    sb = sb.Remove(sb.Length - 1, 1);
                    sb.Append("},");
                }
                sb = sb.Remove(sb.Length - 1, 1);
            }
            sb.Append("]");
            return sb.ToString();
        }
        /// <summary>
        /// 数据表 转 JSON [序列化数据表]
        /// 数据格式：
        /// {"total":"9","rows":[{"l":"2","UserId":"admin","UserName":"系统管理员","Password":"1","DepartId":"7","Gender":"1","RoleId":"2","UserStateId":"1","Photo":"&nbsp;"},{"l":"3","UserId":"jiyaoqin","UserName":"姬耀钦","Password":"123","DepartId":"9","Gender":"1","RoleId":"1","UserStateId":"1","Photo":"&nbsp;"}]}
        /// </summary>
        /// <param name="dt">数据表结构</param>
        /// <param name="dtName">表名称</param>
        /// <returns></returns>
        public static string DataTableToJSON(DataTable dt)
        {
            return DataTableToJSON(dt, string.Empty);
        }
        /// <summary>
        /// 数据表 转 JSON [序列化数据表]
        /// 数据格式：
        /// {"total":"9","rows":[{"l":"2","UserId":"admin","UserName":"系统管理员","Password":"1","DepartId":"7","Gender":"1","RoleId":"2","UserStateId":"1","Photo":"&nbsp;"},{"l":"3","UserId":"jiyaoqin","UserName":"姬耀钦","Password":"123","DepartId":"9","Gender":"1","RoleId":"1","UserStateId":"1","Photo":"&nbsp;"}]}
        /// </summary>
        /// <param name="dt">数据表结构</param>
        /// <param name="dtName">表名称</param>
        /// <returns></returns>
        public static string DataTableToJSON(DataTable dt, string dtName)
        {
            bool HasName = !string.IsNullOrEmpty(dtName);

            StringBuilder sb = new StringBuilder();
            if (HasName)
            {
                sb.Append("{\"");
                sb.Append(dtName);
                sb.Append("\":");
            }
            sb.Append("[");
            if (dt.Rows.Count > 0)
            {
                foreach (DataRow dr in dt.Rows)
                {
                    sb.Append("{");
                    foreach (DataColumn dc in dr.Table.Columns)
                    {
                        sb.Append("\"");
                        sb.Append(dc.ColumnName);
                        sb.Append("\":\"");
                        if (dr[dc] != null && dr[dc] != DBNull.Value && dr[dc].ToString() != "")
                            sb.Append(dr[dc]);
                        else
                            sb.Append("&nbsp;");
                        sb.Append("\",");
                    }
                    sb = sb.Remove(sb.Length - 1, 1);
                    sb.Append("},");
                }
                sb = sb.Remove(sb.Length - 1, 1);
            }

            sb.Append("]");
            if (HasName)
                sb.Append("}");

            return sb.ToString();
        }
